package find_the_difference

import (
	"testing"
)

var tests = []struct {
	s    string
	t    string
	want byte
}{
	{
		"abcd", "abcde", 'e',
	},
	{
		"", "y", 'y',
	},
	{
		"a", "aa", 'a',
	},
	{
		"",
		"",
		't',
	},
}

func TestFindTheDifference(t *testing.T) {
	for _, tt := range tests {
		got := findTheDifference(tt.s, tt.t)
		if got != tt.want {
			t.Fatalf("findTheDifference(%v, %v) = %v, want %v", tt.s, tt.t, got, tt.want)
		}
	}
}
