package greatest_letter

import (
	"testing"
)

var tests = []struct {
	s    string
	want string
}{
	{
		"lEeTcOdE", "E",
	},
	{
		"arRAzFif", "R",
	},
	{
		"AbCdEfGhIjK", "",
	},
	{
		"", "A",
	},
	{
		"", "",
	},
}

func TestFloodFill(t *testing.T) {
	for _, tt := range tests {
		got := greatestLetter(tt.s)
		if got != tt.want {
			t.Fatalf("greatestLetter(%v) = %v, want %v", tt.s, got, tt.want)
		}
	}
}
